use leptos::{component, view, IntoView, Props};

pub const DONATE_MODAL_ID: &str = "contribution_modal";

#[component]
pub fn DonateModal() -> impl IntoView {
	view! {
		<dialog id=DONATE_MODAL_ID class="modal">
			<div class="modal-box">
				<div class="flex flex-col space-y-4">
					<h1 class="text-xl font-bold">Support RustyTube</h1>
					<div class="flex flex-col space-y-8">
						<p>
							RustyTube is a free and
							<a
								href="https://github.com/opensourcecheemsburgers/RustyTube"
								class="link link-primary no-underline px-2"
							>
								open-source
							</a>
							project. It has taken hundreds of hours in development time. Support the project and its further development.
						</p>
						<div class="flex flex-col space-y-4">
							<h2 class="text-lg font-semibold">Liberapay</h2>
							<LiberapaySvg/>
						</div>
						<div class="flex flex-col">
							<h2 class="text-lg font-semibold">Monero</h2>
							<MoneroAddressSvg/>
							<p class="mt-4 break-words rounded bg-neutral p-2 text-sm">
								42bhoxB2DT125WAxLHHZAm2jUpEWUJBe1HAKtmiZsjW6X2r3z8FBqQFEhZdkywuQrrKfnehhhaJexQjVKpahBooq3zYKNjf
							</p>
						</div>
						<div class="flex flex-col">
							<h2 class="text-lg font-semibold">Bitcoin</h2>
							<BitcoinAddressSvg/>
							<p class="break-words rounded bg-neutral p-2 text-sm">
								bc1q8r90zc8j8a2rvkq4ds8374pxh3rpccxgnjx5x2
							</p>
						</div>
					</div>
					<form method="dialog">
						<button class="btn btn-md btn-primary btn-block">
							Got it.
						</button>
					</form>
				</div>
			</div>
		</dialog>
	}
}

